mapper.insert:
/* insertAccount */
INSERT 
  INTO ACCOUNT 
  	 (	MEMBER_ID
  	  , TRADE_DATE
  	  , WITHDRAW_AMT
  	  , DIPOSIT_AMT
  	  , AFTER_AMT
  	  , TRADE_COMMENT
  	  )
VALUES
	 (	#{memberId}
	  , DATE_FORMAT(#{tradeDate}, '%Y-%m-%d')
	  , #{withdrawAmt}
	  , #{dipositAmt}
	  , #{afterAmt}
	  , #{tradeComment}
	  )
