mapper.insert:
/* insertAccount */
INSERT 
  INTO ACCOUNT 
  	 (	MEMBER_ID
  	  , TRADE_DATE
  	  , WITHDRAW_AMT
  	  , DEPOSIT_AMT
  	  , AFTER_AMT
  	  , TRADE_COMMENT
  	  )
VALUES

	 (	#{item.memberId}
	  , DATE_FORMAT(#{item.tradeDate}, '%Y-%m-%d%h:%i:%s')
	  , #{item.withdrawAmt}
	  , #{item.depositAmt}
	  , #{item.afterAmt}
	  , #{item.tradeComment}
	  )
